import React, { Component } from 'react'
import { connect } from 'react-redux'
import { generateBill } from '../Redux/actions'

class Bill extends Component {

    componentDidMount = () => {
        const { match, generateBill } = this.props
        console.log(match)
        generateBill(match.params.id)
    }

    render() {
        const { data, match } = this.props
        console.log(data.data)
        return (
            <div className='container pt-5'>
                <div className='row pt-5 mt-5'>
                    <div className='col-5 offset-3'>
                        <div className="card shadow p-3 mb-5 bg-white rounded" >
                            <h5 class="card-title p-3">{match.params.id}</h5>
                            <table className="table table-dark">
                                <thead>
                                    <tr>
                                        <th scope="col"></th>
                                        <th scope="col"></th>
                                        <th scope="col"></th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {
                                        data && data.map(ele =>

                                            <tr key={uuidv4()}>
                                                <td scope="row">{ele.name}</td>
                                                <td>{ele.item}</td>
                                                <td>{ele.price}</td>
                                                <td>{ele.quantity}</td>
                                                <td>{ele.day}</td>
                                                <td onClick={() => this.handleDelete(ele.id)}>Delete</td>
                                                <td><Link to={`/edit/${ele.id}`}>Edit</Link></td>
                                            </tr>
                                        )
                                    }
                                </tbody>
                            </table>
                            {
                                data.data === undefined ?
                                    <h4>user not defined</h4> :
                                    data.data.map(item => (
                                        <div>
                                            {item.item}
                                        </div>

                                    ))
                            }
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

const mapStateToProps = state => ({
    isLoading: state.isLoading,
    data: state.data,
    isError: state.isError

})

const mapDispatchToProps = dispatch => ({
    generateBill: payload => dispatch(generateBill(payload))
})

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(Bill);
