import React, { Component } from 'react'
import { connect } from 'react-redux'
import { showItems } from '../Redux/'
import { Link } from 'react-router-dom'

class Orders extends Component {
    constructor(props) {
        super(props)
    }

    render() {
        const { data, showItems } = this.props

        return (
            <div className="container">
                <p className="text-center m-3">Enter the user name to view orders</p>
                <div className="form-group col-12">
                    <label>Name</label>
                    <input className="form-control"
                        name="name"
                        value={this.state.name}
                        onChange={this.handleChange} />
                    <button className="btn btn-info">Search</button>
                </div>

                <h3 className="text-center m-3">User Details</h3>

                <table className="table table-dark">
                    <thead>
                        <tr>
                            <th scope="col">User Id</th>
                            <th scope="col">Name</th>
                            <th scope="col">Email</th>
                            <th scope="col">Mobile</th>
                            <th scope="col">Age</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            data && data.map(item =>

                                <tr key={item.id}>
                                    <td scope="row"><Link></Link>{item.id}</td>
                                    <td>{item.name}</td>
                                    <td>{item.email}</td>
                                    <td>{item.mobile}</td>
                                    <td>{item.age}</td>
                                </tr>

                            )
                        }
                    </tbody>
                </table>
            </div>
        )
    }
}

const mapStateToProps = state => ({
    isLoading: state.isLoading,
    data: state.data,
    isError: state.isError

})

const mapDispatchToProps = dispatch => ({
    showItems: payload => dispatch(showItems(payload)),
})

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(Orders);
