import React, { Component } from 'react'
import { connect } from 'react-redux'
import { editItem } from '../Redux/actions'
import { itemDetails } from '../Redux/actions'

class EditOrder extends Component {
    constructor(props) {
        super(props)

        this.state = {
            name: '',
            item: '',
            price: '',
            quantity: '',
            day: '',
        }
    }

    handleChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    componentDidMount = () => {
        const { match, itemDetails } = this.props
        itemDetails(match.params.id)
    }

    handleClick = () => {
        const { add } = this.props
        editItem({id:add.id,
            name:this.state.name,
            item:this.state.item,
            price:this.state.price,

        })
    }

    render() {
        const { add } = this.props
        return (
            <>
                <div className="container"
                    onMouseEnter={() => this.setState({
                        name: add.name,
                        item: add.item,
                        price: add.price,
                        quantity: add.price,
                        day: add.day
                    })}>

                    <h3 className="text-center m-3">Enter Item</h3>

                    <div className="border row offset-3 col-md-6 p-3 bg-dark text-light">
                        <div className='col-12'>
                            <div className="form-group col-12">
                                <label>Name</label>
                                <input className="form-control"
                                    name="name"
                                    value={this.state.name}
                                    onChange={this.handleChange} />
                            </div>
                            <div className="form-group col-12">
                                <label>Item Name</label>
                                <input className="form-control"
                                    name="item"
                                    value={this.state.item}
                                    onChange={this.handleChange} />
                            </div>
                            <div className="form-group col-12">
                                <label>Price</label>
                                <input className="form-control"
                                    name="price"
                                    value={this.state.price}
                                    onChange={this.handleChange} />
                            </div>
                            <div className="form-group col-12">
                                <label>Quantity</label>
                                <input className="form-control"
                                    name="quantity"
                                    value={this.state.quantity}
                                    onChange={this.handleChange} />
                            </div>
                            <div className="form-group col-md-4">
                                <label >Day</label>
                                <select className="form-control"
                                    name="day"
                                    value={this.state.day}
                                    onChange={this.handleChange}
                                >
                                    <option >Choose day</option>
                                    <option value="monday">Monday</option>
                                    <option value="tuesday">Tuesday</option>
                                    <option value="wednesday">Wednesday</option>
                                    <option value="thursday">Thursday</option>
                                    <option value="friday">Friday</option>
                                    <option value="saturday">Saturday</option>
                                    <option value="sunday">Sunday</option>

                                </select>
                            </div>
                        </div>

                        <button className="btn btn-info ml-5 mt-3"
                            onClick={this.handleClick}
                        >
                            Add Details
                        </button>
                    </div>
                    {/* <p className="text-center lead m-3">{add.message}</p> */}
                </div>
            </>
        )
    }
}

const mapStateToProps = state => ({
    add: state.add,
});

const mapDispatchToProps = dispatch => ({
    itemDetails: payload => dispatch(itemDetails(payload)),
    editItem:payload => dispatch(editItem(payload))
});

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(EditOrder);
