import {
    ADD_ITEM_SUCCESS, SHOW_ITEM_SUCCESS, EDIT_ITEM_SUCCESS
} from './actionTypes'

export const initState = {
    add: [],
    data:[],
    item:[]
}

export default (state = initState, { type, payload }) => {
    console.log(payload)
    switch (type) {
        case ADD_ITEM_SUCCESS:
            return{
                ...state,
                add:payload
            }

        case SHOW_ITEM_SUCCESS:
            return{
                ...state,
                data:payload.data
            }

        case EDIT_ITEM_SUCCESS:
            return{
                ...state
            }
        default:
            return state
    }
}
