import React, { Component } from 'react'
import { connect } from 'react-redux'
import { showItems } from '../Redux/actions'
import { Link } from 'react-router-dom'

class Orders extends Component {
    constructor(props) {
        super(props)

        this.state = {
            user: ''
        }
    }

    render() {
        const { data, showItems } = this.props

        return (
            <div className="container">
                <p className="m-3">Enter the user name to view orders</p>
                <div className="form-group col-3">
                    <label>Name</label>
                    <input className="form-control"
                        value={this.state.user}
                        onChange={(e) => this.setState({ user: e.target.value })} />
                    <button className="btn btn-info m-2"
                        onClick = {()=>{
                            showItems(this.state.user)
                        }}>
                        Search
                    </button>
                </div>

                <h3 className="text-center m-3">User Details</h3>

                <table className="table table-dark">
                    <thead>
                        <tr>
                            <th scope="col">Name</th>
                            <th scope="col">Item</th>
                            <th scope="col">Price</th>
                            <th scope="col">Age</th>
                            <th scope="col">User Id</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            data && data.map(item =>

                                <tr key={item.id}>
                                    <td scope="row"><Link></Link>{item.id}</td>
                                    <td>{item.name}</td>
                                    <td>{item.email}</td>
                                    <td>{item.mobile}</td>
                                    <td>{item.age}</td>
                                </tr>

                            )
                        }
                    </tbody>
                </table>
            </div>
        )
    }
}

const mapStateToProps = state => ({
    isLoading: state.isLoading,
    data: state.data,
    isError: state.isError

})

const mapDispatchToProps = dispatch => ({
    showItems: payload => dispatch(showItems(payload)),
})

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(Orders);
